#include<bits/stdc++.h>

using namespace std;

class Solution {
public:
    int longestValidParentheses(string s) {
        int s_length = s.size();
        int buffer[s_length + 1];
        int max = 0;

        for (int i = 0; i < s_length; i++) {
            buffer[i] = s[i] == '(' ? 1 : -1;
            for (int j = 0; j < i; j++) {
                if (buffer[j] < 0)
                    continue;

                buffer[j] = buffer[j] + buffer[i];

                if (buffer[j] == 0 && i - j + 1 > max) {
                    max = i - j + 1;
                }
            }
        }

        return max;
    }
};

